import React,{useState,useEffect} from 'react';

import '../css/reset.css';
import '../css/index.css';

import Game from './Game';
import Info from './Info';
import Popup from './Popup';
import AppContext from '../contexts/AppContext';

import {cardSetf} from '../function/cardSet';
import {answerf} from '../function/answer';

// ========================================

const App = () => {
  var cardSet = cardSetf();// TODO: カードを推すたびに呼び出されちゃってる
  const initialState = {
    cardSet:cardSet,//cardの並び
    answer:answerf(cardSet),//答えの組合せ
    userChoice:[null,null],//userの選択
    floor:1
  }
const [state,setState] = useState(initialState);
const [judge,setJudge] = useState(false);//正解か不正解状態
const [open,setOpen] = useState(false);//表示、非表示
const [timer,setTimer] = useState(10);

const timerf = () => {
  setTimer(10);
  // let d = new Date();
  // const endTime = new Date(d.getTime() + timer * 1000);
  let count = timer;
    console.log(count);
  const interval = setInterval(() => {
    count--;
    setTimer(count);
    console.log(count,timer);
    // d = new Date();
    // console.log(d.getTime(),endTime.getTime());
    if(timer === 0 || open) {
      clearInterval(interval);
    }
  }, 1000)
  // setId(interval);
}

const [countdown,setCountdown] = useState(timerf);

  useEffect(//floorを参照して変更があったときに正解不正解を識別
    () => {
      if(state.floor == 1){
        setJudge(false);
      }
      else{
        setJudge(true);
      }
      if(timer == 0){// 時間切れのとき
        timerf();
        setState({
          cardSet:cardSet,
          answer:answerf(cardSet),
          userChoice:[null,null],
          floor:1
        });
        setOpen(true);
        setTimeout(setOpen,1000,false);
      }
    },
    [state.floor,timer]
  );
  // console.log(judge);


  // useEffect(//timer
  //   () => {
  //     timerf();
  //   },[open]
  // );

  return(
    <AppContext.Provider value={{state,setState,open,setOpen,judge,timer,setTimer,countdown,setCountdown}}>
    <div className="game">
      <div className="inner">
        <Game />
        <Info />
        <Popup />
      </div>
    </div>
    </AppContext.Provider>
  );
}

export default App;
