import React,{useState,useContext} from 'react';
import AppContext from '../contexts/AppContext';

// =====================================

const Start = () => {
  const {start,setStart} = useContext(AppContext);

  const handleClickStart = () => {
    setStart(1);
    console.log(start);
  }

  return(
    <div className={'start-modal' + (start === 0 ? ' visible' : '')}>
      <p className="title">Animal game</p>
      <p>ルール説明</p>
      <p>このサイトは、<a href="https://twitter.com/roxasBazooka" target="_blank" rel="noopener noreferrer">saito</a>がReactの学習兼ポートフォリオとして作成したゲームです。</p>
      <a className="button" onClick={handleClickStart}>START</a>
    </div>
  );
}

export default Start;
