import React,{useState,useEffect} from 'react';

import '../css/reset.css';
import '../css/index.css';

import Game from './Game';
import Info from './Info';
import Popup from './Popup';
import AppContext from '../contexts/AppContext';

import {cardSetf} from '../function/cardSet';
import {answerf} from '../function/answer';

// ========================================

export var timerArray = new Array();

const App = () => {
  var cardSet = cardSetf();// TODO: カードを推すたびに呼び出されちゃってる
  const initialState = {
    cardSet:cardSet,//cardの並び
    answer:answerf(cardSet),//答えの組合せ
    userChoice:[null,null],//userの選択
    floor:1
  }
const [state,setState] = useState(initialState);
const [judge,setJudge] = useState(false);//正解か不正解状態
const [open,setOpen] = useState(false);//表示、非表示
const [timer,setTimer] = useState(10);
const [n,setN] = useState(0);

let count;
const timerf = () => {
  console.log('配列数:'+timerArray.length);
  console.log('aiueo');
  console.log('timer'+timer);//なぜかnullが入っている
  console.log('配列数:'+timerArray.length);//0
  count = 10;
  const promise = new Promise((resolve) => {
    timerArray.push(setInterval(() => {
      console.log('配列数:'+timerArray.length);//1
      count--;
      setTimer(count);
      console.log(count,timer);
      console.log('n:'+n);
      if(count === 0) {
        resolve();
        console.log('配列数:'+timerArray.length);
        const reset = setTimeout(() => {//1秒後に時間切れでリセット
          setTimer(10);
          console.log('timer:'+timer);
          setState({
            cardSet:cardSet,
            answer:answerf(cardSet),
            userChoice:[null,null],
            floor:1
          });
          setOpen(true);
          setTimeout(setOpen,1000,false);
        }, 1000);
      }
    }, 1000));
  });
  promise.then(() => {
    console.log('clear');
    //タイマーを止める
    clearInterval(timerArray.shift());
  });
    // clearInterval(interval);
}
  useEffect(//floorを参照して変更があったときに正解不正解を識別
    () => {
      if(state.floor == 1){
        setJudge(false);
      }
      else{
        setJudge(true);
      }
      // else if (open === true) {
      //   // timerf();
      // }
    },
    [state.floor]
  );

  useEffect(//timer
    () => {
      if(timer === 10){
        console.log('timerf:'+timer);
        timerf();
      }
    },[timer]
  );

  return(
    <AppContext.Provider value={{state,setState,open,setOpen,judge,timer,setTimer,n,setN}}>
    <div className="game">
      <div className="inner">
        <Game />
        <Info />
        <Popup />
      </div>
    </div>
    </AppContext.Provider>
  );
}

export default App;
