import React,{useContext} from 'react';
import AppContext from '../contexts/AppContext';

// =====================================

const Result = () => {
  const {state,start,setStart,result,setResult} = useContext(AppContext);

  const handleClickRule = () => {
    setStart(0);
  }
  const handleClickStart = () => {
    setStart(1);
  }

// reroadした時にresultがでないように
  return(
    <div className={'start-modal' + (start === 2 ? '' : ' none') + (result === 1 ? ' hidden' : '')}>
      <p className="title">Animal game!</p>
      <div className="content">
        <p className="head">Result</p>
        <p>{result + '階層まで進めました！'}</p>
      </div>
      <a className="button" onClick={handleClickRule}>Rule</a>
      <a className="button" onClick={handleClickStart}>Try again</a>
    </div>
  );
}

export default Result;
