import React,{useContext,useEffect} from 'react';



import AppContext from '../contexts/AppContext';


// ================================

const Timer = () => {
  const {state,timer,setTimer} = useContext(AppContext);


  useEffect(
    () => {
      let d = new Date();
      const endTime = new Date(d.getTime() + timer * 1000);
      let count = timer;
      const id = setInterval(() => {
        count--
        setTimer(count);

        d = new Date();
        console.log(d.getTime(),endTime.getTime());
        if(d.getTime() >= endTime.getTime()) {
          clearInterval(id);
        }
      }, 1000)
    },[state.floor]
  );


  return(
    <div className="timer-area">
      {timer}
    </div>
  );
}


export default Timer;
