import React,{useState,useEffect} from 'react';

import '../css/reset.css';
import '../css/index.css';

import Game from './Game';
import Info from './Info';
import Popup from './Popup';
import AppContext from '../contexts/AppContext';

import {cardSetf} from '../function/cardSet';
import {answerf} from '../function/answer';

// ========================================

const App = () => {
  var cardSet = cardSetf();// TODO: カードを推すたびに呼び出されちゃってる
  const initialState = {
    cardSet:cardSet,//cardの並び
    answer:answerf(cardSet),//答えの組合せ
    userChoice:[null,null],//userの選択
    floor:1
  }
const [state,setState] = useState(initialState);
const [judge,setJudge] = useState(false);//正解か不正解状態
const [open,setOpen] = useState(false);//表示、非表示
const [timer,setTimer] = useState(10);

const timerf = () => {
  console.log(timer);//なぜかnullが入っている
  let count = 10;
    console.log(count);
  const interval = setInterval(() => {
    count--;
    setTimer(count);
    console.log(count,timer,open);
    if(count === 0) {
      clearInterval(interval);
      const reset = setTimeout(() => {//1秒後に時間切れでリセット
        setTimer(10);
        setState({
          cardSet:cardSet,
          answer:answerf(cardSet),
          userChoice:[null,null],
          floor:1
        });
        setOpen(true);
        setTimeout(setOpen,1000,false);
      }, 1000);
    }
    // if(open === true) {
    //   clearInterval(interval);
    //
    // }
  }, 1000);
    // clearInterval(interval);
}
  useEffect(//floorを参照して変更があったときに正解不正解を識別
    () => {
      if(state.floor == 1){
        setJudge(false);
      }
      else{
        setJudge(true);
      }
      if(timer == 10){
        timerf();
      }
      if(timer == -1){// 時間切れのとき
        // console.log(timer);//なぜかnullが入っている
        // setTimer(10);
        // setState({
        //   cardSet:cardSet,
        //   answer:answerf(cardSet),
        //   userChoice:[null,null],
        //   floor:1
        // });
        // setOpen(true);
        // setTimeout(setOpen,1000,false);
      }
      else if (open === true) {
        // timerf();
      }
    },
    [state.floor,timer,open]
  );


  // useEffect(//timer
  //   () => {
  //     if(open === false ){
  //       // setTimer(10);
  //       console.log(timer);//なぜかnullが入っている
  //       timerf();
  //     }
  //   },[open]
  // );

  return(
    <AppContext.Provider value={{state,setState,open,setOpen,judge,timer,setTimer}}>
    <div className="game">
      <div className="inner">
        <Game />
        <Info />
        <Popup />
      </div>
    </div>
    </AppContext.Provider>
  );
}

export default App;
